#!/bin/bash
set -e -u -o pipefail

here="$(dirname "${BASH_SOURCE[0]}")"

cat >$here/_oasis <<EOF
#AUTOGENERATED FILE; EDIT oasis.sh INSTEAD
OASISFormat:  0.2
OCamlVersion: >= 3.12
Name:         typehashlib
Version:      0.1.1
Synopsis:     Syntax extension for deriving "typehash" functions automatically.
Authors:      Jane street capital
Copyrights:   (C) 2009-2011 Jane Street Capital LLC
License:      LGPL-2.1 with OCaml linking exception
LicenseFile:  LICENSE
Plugins:      StdFiles (0.2),
              DevFiles (0.2),
              META (0.2)
XStdFilesREADME: false
XStdFilesAUTHORS: false
XStdFilesINSTALLFilename: INSTALL
BuildTools:   ocamlbuild

Library typehashlib
  Path:               lib
  Modules:
  FindlibName:        typehashlib

Library pa_typehash
  Path:               syntax
  Modules:            Pa_typehash
  FindlibParent:      typehashlib
  FindlibName:        syntax
  BuildDepends:       camlp4.lib,
                      camlp4.quotations,
                      type-conv (>= 2.0.1)
  CompiledObject:     byte
  XMETAType:          syntax
  XMETARequires:      camlp4,type-conv,typehashlib
  XMETADescription:   Syntax extension for "with typehash"
EOF

cat >$here/_tags <<EOF
# OASIS_START
# OASIS_STOP
<syntax/pa_typehash.ml>: syntax_camlp4o,pkg_camlp4.extend
EOF

mkdir -p $here/lib

cd $here
oasis setup
./configure "$@"

